// Import necessary modules
import React from 'react';
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';

// Define the steps for the chatbot
const steps = [
  {
    id: '1',
    message: 'Welcome to the eVisa Processing System! What is your full name?',
    trigger: 'name',
  },
  {
    id: 'name',
    user: true,
    trigger: '3',
  },
  {
    id: '3',
    message: 'Hello {previousValue}! Please select the type of visa you are applying for:',
    trigger: 'visaType',
  },
  {
    id: 'visaType',
    options: [
      { value: 'tourist', label: 'Tourist Visa' },
      { value: 'business', label: 'Business Visa' },
      { value: 'student', label: 'Student Visa' },
    ],
    trigger: '5',
  },
  {
    id: '5',
    message: 'Great! Now, please enter your passport number:',
    trigger: 'passport',
  },
  {
    id: 'passport',
    user: true,
    trigger: '7',
  },
  {
    id: '7',
    message: 'Please wait while we process your visa application...',
    end: true,
  },
];

// Define the theme for the chatbot
const theme = {
  background: '#F5F8FB',
  headerBgColor: '#197B22',
  headerFontColor: '#fff',
  headerFontSize: '22px',
  botBubbleColor: '#0F3789',
  botFontColor: '#fff',
  userBubbleColor: '#FF5733',
  userFontColor: '#fff',
};

// Define additional configurations for the chatbot
const config = {
  botAvatar: 'img.png',
  floating: true,
};

// Define the App component
function App() {
  return (
    <div className="App">
      <ThemeProvider theme={theme}>
        <ChatBot
          headerTitle="eVisa Bot"
          steps={steps}
          {...config}
        />
      </ThemeProvider>
    </div>
  );
}

// Export the App component
export default App;
