// Chatbot.js

import React from 'react';
import ChatBot from 'react-simple-chatbot';
import { ThemeProvider } from 'styled-components';

const ChatbotComponent = () => {
  const theme = {
    background: '#f5f8fb',
    fontFamily: 'Arial',
    headerBgColor: '#197B22',
    headerFontColor: '#fff',
    headerFontSize: '20px',
    botBubbleColor: '#0F3789',
    botFontColor: '#fff',
    userBubbleColor: '#FF5733',
    userFontColor: '#fff',
  };

  const steps = [
    {
      id: '0',
      message: 'Welcome to the e-Visa Processing System! How can I assist you today?',
      trigger: 'menu',
    },
    {
      id: 'menu',
      options: [
        { value: 'aboutVisa', label: 'About Visa', trigger: 'aboutVisa' },
        // Add more options as needed
      ],
    },
    {
      id: 'aboutVisa',
      message: 'An e-Visa is an official document permitting entry into and travel within a certain country. It is an alternative to visas issued at the embassies or consulates. Would you like more information?',
      trigger: 'aboutVisaOptions',
    },
    {
      id: 'aboutVisaOptions',
      options: [
        { value: 'yes', label: 'Yes', trigger: 'moreAboutVisa' },
        { value: 'no', label: 'No', trigger: 'menu' },
      ],
    },
    {
      id: 'moreAboutVisa',
      message: 'Please visit our website for more detailed information about e-Visas.',
      end: true,
    },
  ];

  return (
    <ThemeProvider theme={theme}>
      <ChatBot steps={steps} />
    </ThemeProvider>
  );
};

export default ChatbotComponent;
